(171, 'Máy đo sắt thang thấp HANNA HI746', 100, 0, 'may-do-sat-thang-thap-hanna-hi746', 3500000, 0, '28', '2024-04-21__hannahi746-may-do-sat-thang-thap-hanna-hi746-0-999-ppb-125730.jpg', 0, 0, 1, 1, 0, 'Bảo Hành: 1 Năm', '<p>Dải đo: 0 đến 999 ppb Fe&nbsp;* 1 ppb = 0.001 mg/L (ppm)</p>\r\n\r\n<p>Độ ph&acirc;n giải: 1 ppb</p>\r\n\r\n<p>Độ ch&iacute;nh x&aacute;c (ở nhiệt độ m&ocirc;i trường 25&deg;C/77&deg;F): &plusmn;20 ppb v&agrave; &plusmn;5% gi&aacute; trị đo</p>\r\n\r\n<p>Nguồn đ&egrave;n: LED @ 575nm</p>\r\n\r\n<p>Đầu d&ograve; &aacute;nh s&aacute;ng:&nbsp;silicon photocell</p>\r\n\r\n<p>Điều kiện m&ocirc;i trường: 0 ~ 50&deg;C (32 ~ 122&deg;F); RH max 95% kh&ocirc;ng ngưng tụ</p>\r\n\r\n<p>Loại pin: 1 pin&nbsp;1.5V AAA</p>\r\n\r\n<p>Tự động tắt: sau 10 ph&uacute;t kh&ocirc;ng sử dụng</p>\r\n\r\n<p>K&iacute;ch thước:&nbsp;86.0 x 61.0 x 37.5 mm (3.4 x 2.4 x 1.5&rdquo;)</p>\r\n\r\n<p>Khối lượng: 52 g (1.84 oz.)</p>\r\n\r\n<p><strong>Cung cấp bao gồm:</strong></p>\r\n\r\n<p>- M&aacute;y đo HI746</p>\r\n\r\n<p>- 2 cuvet mẫu c&oacute; nắp</p>\r\n\r\n<p>- 25 g&oacute;i thuốc thử sắt LR HI746-0 cho 25 lần đo</p>\r\n\r\n<p>- Pin (trong m&aacute;y)</p>\r\n\r\n<p>- Hướng dẫn sử dụng</p>\r\n\r\n<p>- Phiếu bảo h&agrave;nh</p>\r\n\r\n<p>- Hộp đựng bằng nhựa.</p>\r\n\r\n<p><strong>Phụ kiện mua th&ecirc;m:</strong></p>\r\n\r\n<p>- HI746-25: thuốc thử sắt LR&nbsp;cho 25 lần đo</p>\r\n\r\n<p>Chi tiết</p>\r\n\r\n<p><strong>MANUAL</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, NULL, 5, '1', '2024-03-21 01:25:40', '2024-04-20 19:42:25'),
(172, 'Bộ Kiểm Tra Sắt (Fe²⁺ & Fe³⁺) Thang Thấp HANNA HI38039', 100, 0, 'bo-kiem-tra-sat-fe2-fe3-thang-thap-hanna-hi38039', 3500000, 0, '29', '2024-04-21__hannahi38039-bo-kiem-tra-sat-fe2-fe3-thang-thap-hanna-hi38039-47766.jpg', 0, 0, 1, 1, 0, 'Bảo Hành: 1 Năm', '<p>Model: HI38039<br />\r\nH&atilde;ng sản xuất: Hanna</p>\r\n\r\n<table border=\"1\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:692px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>Thang đo</p>\r\n			</td>\r\n			<td>\r\n			<p>0.00 to 1.00 mg/L (ppm)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Độ ph&acirc;n giải</p>\r\n			</td>\r\n			<td>\r\n			<p>0.02 mg/L (ppm)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Phương ph&aacute;p</p>\r\n			</td>\r\n			<td>\r\n			<p>phenanthroline</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Số ph&eacute;p đo</p>\r\n			</td>\r\n			<td>\r\n			<p>100</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Loại CTK&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p>Đĩa soi m&agrave;u</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Khối lượng</p>\r\n			</td>\r\n			<td>\r\n			<p>436 g</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Cung cấp gồm</p>\r\n			</td>\r\n			<td>\r\n			<p>HI38039 cung cấp k&egrave;m 100 g&oacute;i thuốc thử sắt, đĩa checker, ống thủy tinh c&oacute; nắp (2), v&agrave; pipet nhựa 3 mL.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chi tiết</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 0, NULL, 5, '1', '2024-03-21 01:25:40', '2024-04-20 19:42:50'),
(173, 'Máy Quang Đo Sắt Thang Cao Trong Nước Sạch HANNA HI97721', 100, 0, 'may-quang-do-sat-thang-cao-trong-nuoc-sach-hanna-hi97721', 2800000, 0, '29', '2024-04-21__hannahi97721-may-quang-do-sat-thang-cao-trong-nuoc-sach-hanna-hi97721-0-5mg-l-0-01-mg-l-101949.jpg', 0, 0, 1, 1, 0, 'Bảo Hành: 1 Năm', '<p>Dải đo: 0.00~5.00 mg/L (Fe)</p>\r\n\r\n<p>Độ ph&acirc;n giải: 0.01 mg/L</p>\r\n\r\n<p>Độ ch&iacute;nh x&aacute;c tại 25&deg;C: &plusmn;0.04 mg/L &plusmn;2% theo số đọc</p>\r\n\r\n<p>Nguồn s&aacute;ng: điốt ph&aacute;t quang</p>\r\n\r\n<p>Bộ lọc th&ocirc;ng dải: 525nm</p>\r\n\r\n<p>Băng th&ocirc;ng bộ lọc th&ocirc;ng dải: 8nm</p>\r\n\r\n<p>Độ ch&iacute;nh x&aacute;c bước s&oacute;ng bộ lọc th&ocirc;ng dải: &plusmn;1.0nm</p>\r\n\r\n<p>M&aacute;y d&ograve; &aacute;nh s&aacute;ng: Tế b&agrave;o quang silicon</p>\r\n\r\n<p>Loại cuvet: tr&ograve;n, đường k&iacute;nh 24.6mm (b&ecirc;n trong 22mm)</p>', 0, NULL, 5, '1', '2024-03-21 01:25:40', '2024-04-20 19:43:10'),
(174, 'Máy lọc nước thương mại Maxtream Twin EP-20', 100, 0, 'may-loc-nuoc-thuong-mai-maxtream-twin-ep-20', 2933000, 0, '11', '2024-04-21__may-loc-nuoc-cong-suat-lon-maxtream-twin-ep-20-768x768.jpg', 0, 0, 1, 1, 0, 'Bảo Hành: 1 Năm', '<p>Th&ocirc;ng tin sản phẩm</p>\r\n\r\n<h2><strong>M&aacute;y lọc nước c&ocirc;ng suất lớn Maxtream Twin EP-20 &ndash; Đ&aacute;p ứng đầy đủ nhu cầu nước sạch với c&ocirc;ng suất lọc ấn tượng!</strong></h2>\r\n\r\n<p><strong>M&aacute;y lọc nước c&ocirc;ng suất lớn Maxtream Twin EP-20</strong>&nbsp;sử dụng cho c&aacute;c&nbsp; gia đ&igrave;nh, nh&agrave; h&agrave;ng, qu&aacute;n bar v&agrave; tiệm đồ uống. Sản phẩm ch&iacute;nh l&agrave; giải ph&aacute;p tốt nhất với chi ph&iacute; hợp l&yacute; gi&uacute;p mang đến nguồn nước chất lượng cao v&agrave; hiệu suất lọc tuyệt vời v&agrave; v&ocirc; c&ugrave;ng tiện dụng.</p>\r\n\r\n<p>Sản phẩm được cung cấp ch&iacute;nh h&atilde;ng tại Smart Việt với đầy đủ những giấy tờ cần thiết, cũng như chất lượng được kiểm định kỹ c&agrave;ng. Li&ecirc;n hệ với ch&uacute;ng t&ocirc;i để c&oacute; được sản phẩm lọc nước ph&ugrave; hợp v&agrave; nhận nhiều ưu đ&atilde;i hấp dẫn!</p>\r\n\r\n<p><img alt=\"Máy lọc nước thương mại Maxtream Twin EP-20\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-cong-suat-lon-maxtream-ep-20.jpg\" style=\"height:600px; width:600px\" /></p>', 0, NULL, 5, '1', '2024-03-21 01:25:40', '2024-04-20 19:44:00'),
(175, 'Máy lọc nước nguyên khoáng Picogram PNP (Công nghệ UF)', 99, 3, 'may-loc-nuoc-nguyen-khoang-picogram-pnp-cong-nghe-uf', 3450000, 0, '11', '2024-04-21__01-may-loc-nuoc-picogram-pnp-han-quoc.jpg', 0, 0, 1, 1, 1, 'Bảo Hành: 1 Năm', '<p>Th&ocirc;ng tin sản phẩm</p>\r\n\r\n<h2>M&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF mang lại nguồn nước tốt cho sức khỏe!</h2>\r\n\r\n<p><strong>M&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF</strong>&nbsp;được thiết kế phần l&otilde;i lọc c&oacute; k&iacute;ch thước từ 0,1~ 0,001 Micron (&micro;m), lọc sạch c&aacute;c tạp chất c&oacute; k&iacute;ch thước si&ecirc;u nhỏ, loại bỏ tối đa c&aacute;c chất dầu, mỡ, hydroxit kim loại, chất keo, nhũ tương, chất rắn lơ lửng&hellip; v&agrave; đặc biệt l&agrave; c&oacute; thể triệt ti&ecirc;u được vi khuẩn tới 99.9%. C&aacute;c l&otilde;i lọc, cấp lọc v&agrave; m&agrave;ng lọc UF sẽ chặn lại tối đa c&aacute;c loại tạp chất, virus, vi khuẩn, từ đ&oacute; cho ra một nguồn nước gi&agrave;u kho&aacute;ng chất, đảm bảo tốt cho sức khỏe của người sử dụng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2023/01/MAY-LOC-NUOC-PICOGRAM-PNP.png\" style=\"height:910px; width:640px\" /></p>\r\n\r\n<p>M&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF gi&uacute;p cho nguồn nước uống đầu ra kh&ocirc;ng c&ograve;n m&ugrave;i h&ocirc;i tanh, b&atilde; nhờn.</p>\r\n\r\n<p><strong>Smart Việt</strong>&nbsp;l&agrave; nh&agrave; ph&acirc;n phối ch&iacute;nh h&atilde;ng của thương hiệu&nbsp;<strong>Picogram đến từ H&agrave;n Quốc</strong>. V&agrave; đ&acirc;y cũng l&agrave; sản phẩm được kh&aacute;ch h&agrave;ng lựa chọn, c&oacute; những đ&aacute;nh gi&aacute; rất cao trong qu&aacute; tr&igrave;nh sử dụng, l&agrave; một sản phẩm m&aacute;y lọc nước gia đ&igrave;nh tuyệt vời</p>\r\n\r\n<p><img alt=\"Máy lọc nước Picogram Nhập khẩu Hàn Quốc\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/may-loc-nuoc-picogram7.jpg\" style=\"height:245px; width:700px\" /></p>\r\n\r\n<p><em>M&aacute;y lọc nước Picogram Nhập khẩu H&agrave;n Quốc</em></p>\r\n\r\n<h3>Ưu điểm nổi trội của m&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF l&agrave; c&oacute; m&agrave;ng lọc si&ecirc;u hiện đại!</h3>\r\n\r\n<p>M&aacute;y lọc nước Picogram UF được ứng dụng c&ocirc;ng nghệ lọc Nano kết hợp m&agrave;ng si&ecirc;u lọc UF (Ultrafiltration), nhờ vậy c&aacute;c loại vi khuẩn, c&aacute;c chất độc hại sẽ được được loại bỏ, mang tới nguồn nước tinh khiết uống trực tiếp kh&ocirc;ng cần đun s&ocirc;i.</p>\r\n\r\n<p>Thiết kế m&aacute;y với kiểu d&aacute;ng nhỏ gọn, tiện lợi, thiết kế van tự ngắt nước khi thay l&otilde;i lọc, c&ugrave;ng với thiết kế l&otilde;i lọc xoay 90 độ, gi&uacute;p bạn dễ d&agrave;ng trong thao t&aacute;c thay mới bộ l&otilde;i lọc.</p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2023/01/THAY-LOI-PICOGRAM-PNP.png\" style=\"height:500px; width:500px\" /></p>\r\n\r\n<p><em>M&aacute;y lọc nước Picogram thay l&otilde;i dễ d&agrave;ng</em></p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;M&aacute;y lọc kh&ocirc;ng c&oacute; nước thải, gi&uacute;p gia đ&igrave;nh bạn tiết kiệm được h&oacute;a đơn điện nước h&agrave;ng th&aacute;ng, gi&uacute;p tiết kiệm t&agrave;i nguy&ecirc;n nước.</p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Quy tr&igrave;nh lắp đặt, vận h&agrave;nh sản phẩm đơn giản, nhanh ch&oacute;ng v&agrave; thuận tiện.</p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Cấu tr&uacute;c v&agrave; vật liệu m&agrave;ng lọc đồng nhất sử dụng phương ph&aacute;p lọc cơ học l&ecirc;n kh&ocirc;ng l&agrave;m biến đổi t&iacute;nh chất h&oacute;a học của nguồn nước</p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Vật liệu của m&agrave;ng lọc kh&ocirc;ng x&acirc;m nhập v&agrave;o nguồn nước, đảm bảo trong suốt qu&aacute; tr&igrave;nh xử l&yacute;.</p>\r\n\r\n<h3>M&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF đến từ H&agrave;n Quốc được nghi&ecirc;n cứu d&agrave;nh ri&ecirc;ng cho nguồn nước Việt Nam:</h3>\r\n\r\n<p>Với mong muốn đem lại nguồn nước sạch tốt nhất đến với c&aacute;c gia đ&igrave;nh Việt Nam, từ th&aacute;ng 6/2014-6/2015 Smart Việt đ&atilde; lấy c&aacute;c mẫu nước gửi qua h&atilde;ng Picogram để c&ugrave;ng nghi&ecirc;n cứu v&agrave; đưa ra một sản phẩm ph&ugrave; hợp nhất với nguồn nước Việt Nam.</p>\r\n\r\n<p>Từ th&aacute;ng 1/2016 sản phẩm m&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF đ&atilde; c&oacute; mặt tại thị trường Việt Nam, l&agrave; niềm từ h&agrave;o lớn khi đem đến người sử dụng 1 m&aacute;y lọc nước với chất lượng nước lu&ocirc;n đảm bảo v&agrave; đạt hiệu quả xử l&yacute; cao nhất</p>\r\n\r\n<p>L&otilde;i lọc sử dụng trong m&aacute;y Picogram UF được đ&uacute;c nguy&ecirc;n khối v&agrave; lắp r&aacute;p ho&agrave;n thiện tại H&agrave;n Quốc đạt chứng nhận CO: Made in Korea.<br />\r\nM&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF sẽ đem đến cho người ti&ecirc;u d&ugrave;ng VIệt Nam chất lượng nước tốt nhất theo ti&ecirc;u chuẩn của H&agrave;n Quốc v&agrave; của Bộ Y Tế Việt Nam, gi&uacute;p bảo vệ sức khỏe người sử dụng.</p>\r\n\r\n<p><em><strong>Sử dụng m&aacute;y lọc nước Picogram nhằm xử l&yacute; c&aacute;c vấn đề:</strong></em></p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Nắp đặt được ở nhiều vị tr&iacute; kh&aacute;ch nhau: dưới gầm trậu, treo tường &hellip;<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Loại bỏ c&aacute;c tạp chất, rong r&ecirc;u, cặn bẩn, m&agrave;u, m&ugrave;i vị lạ&hellip;<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Loại bỏ kim loại nặng nhưng vẫn giữ được lượng nhất định trong ngưỡng c&oacute; lợi cho cơ thể.<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Lọc c&aacute;c th&agrave;nh phần h&oacute;a học, chất hữu cơ, thuốc trừ s&acirc;u, clo, amoni, asen&hellip;<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Diệt vi khuẩn, vi r&uacute;t c&oacute; hại 99,9%<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Giữ kho&aacute;ng cần thiết cho cơ thể, kho&aacute;ng canxi ở mức tốt nhất cho cơ thể sử dụng.<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Tạo nước kiềm h&oacute;a c&oacute; PH từ 7-8,5 gi&uacute;p trung h&ograve;a axit hỗ trợ điều trị bệnh mỡ m&aacute;u, go&uacute;t, tiểu đường&hellip;ngăn ngừa gốc tự do<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Tạo gi&aacute; trị gia tăng cho sản phẩm, kinh tế v&agrave; tiếp kiệm được chi ph&iacute; nước thải v&agrave; ti&ecirc;u hao điện cũng như chi ph&iacute; bảo tr&igrave;<br />\r\n<img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/nut-tich-1.jpg\" style=\"height:13px; width:20px\" />&nbsp;Mẫu m&atilde; đẹp v&agrave; nhỏ gọn mang thương hiệu H&agrave;n Quốc &ndash; USA (Sản phẩm được chuyển giao c&ocirc;ng nghệ USA)</p>\r\n\r\n<p><strong><em>Khả năng xử l&yacute; ưu việt của M&aacute;y lọc nước Picogram đến từ H&agrave;n Quốc so với c&aacute;c m&aacute;y lọc nước truyền thống:</em></strong></p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/may-loc-nuoc-picogram-9.jpg\" style=\"height:713px; width:820px\" /></p>\r\n\r\n<h3><strong>Hệ thống lọc hiện đại của m&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram PNP:</strong></h3>\r\n\r\n<p><strong>M&aacute;y lọc nước Picogram PNP</strong>&nbsp;&nbsp;sử dụng hệ thống 04 l&otilde;i lọc (Gồm Sediment filter, Pre-carbon filter, UF&nbsp;membrane filter, Post-carbon filer) + 1 l&otilde;i lọc&nbsp;phụ b&ecirc;n ngo&agrave;i</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>Stt</strong></td>\r\n			<td><strong>Th&ocirc;ng tin l&otilde;i lọc</strong></td>\r\n			<td><strong>H&igrave;nh ảnh</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1</strong></td>\r\n			<td><strong>L&otilde;i lọc Sediment Filter</strong>\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&ndash; Sediment Với t&aacute;c dụng lọc sạch gấp 3-4 lần l&otilde;i lọc c&ugrave;ng loại, với c&ocirc;ng nghệ sản xuất kh&eacute;p k&iacute;n vật liệu lọc</p>\r\n\r\n			<p>Polome cao cấp độc quyền được bao bọc bởi lớp nhựa nguy&ecirc;n sinh ABS</p>\r\n\r\n			<p>&ndash; L&otilde;i c&oacute; t&aacute;c dụng loại bỏ to&agrave;n bộ: rỉ s&eacute;t, r&ecirc;u, nấm mốc&hellip; Giữ lại to&agrave;n bộ vật chất c&oacute; k&iacute;ch thước &lt; 5 micron</p>\r\n\r\n			<p>&ndash; L&otilde;i Sediment được ch&iacute;nh WQA chứng thực đại đủ ti&ecirc;u&nbsp; chuẩn NSF/ANSI Standard 42</p>\r\n\r\n			<p>&ndash; Tuổi thọ l&otilde;i: 6 th&aacute;ng/1.800 l&iacute;t</p>\r\n			</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf.jpg\" style=\"height:254px; width:61px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2</strong></td>\r\n			<td>\r\n			<p><strong>L&otilde;i lọc&nbsp;Pre Carbon FIlter</strong></p>\r\n\r\n			<p><strong>&ndash;&nbsp;</strong>Pre Carbon được h&igrave;nh th&agrave;nh từ than hoạt t&iacute;nh cao cấp &eacute;p dạng h&igrave;nh khối c&oacute; nhiều&nbsp;lỗ nhỏ&nbsp;c&oacute; t&aacute;c dụng k&eacute;p gi&uacute;p ngăn ngừa t&aacute;i nhiễm&nbsp; bẩn v&agrave; hấp thụ với c&aacute;c chất liệu cao cấp l&otilde;i được cấu tạo&nbsp;gồm 2 tầng&nbsp;lọc:</p>\r\n\r\n			<p>+ Với lớp m&agrave;ng lọc kết d&iacute;nh si&ecirc;u nhỏ tại ngay nửa đầu tr&ecirc;n của l&otilde;i sẽ d&ugrave;ng để ngăn cặn bẩn hay vi khuẩn t&aacute;i x&acirc;m nhập, gi&uacute;p giảm thiểu lượng h&oacute;a chất tồn dư ở trong nước</p>\r\n\r\n			<p>+ L&otilde;i Pre Carbon hấp phụ v&agrave; khử m&ugrave;i h&ocirc;i gi&uacute;p loại bỏ to&agrave;n&nbsp; bộ c&aacute;c chất g&acirc;y ung thư như Assen, kim loại nặng&hellip; khử ph&oacute;ng xạ v&agrave; những h&oacute;a chất hữu cơ v&agrave; lượng Clo bị tồn dư trong nước</p>\r\n\r\n			<p>&ndash; Tuổi thọ của l&otilde;i: 9-12 th&aacute;ng/3.600 l&iacute;t</p>\r\n			</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-1.jpg\" style=\"height:267px; width:64px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>3</strong></td>\r\n			<td>\r\n			<p><strong>L&otilde;i lọc UF membrane filter</strong></p>\r\n\r\n			<p>&ndash; M&agrave;ng lọc UF l&agrave; m&agrave;ng lọc si&ecirc;u sợi rỗng Với k&iacute;ch thước từ 0,1~ 0,001 Micron (&micro;m) m&agrave;ng lọc UF c&oacute; thể lọc sạch c&aacute;c tạp chất c&oacute; k&iacute;ch thước nhỏ hơn cả vi khuẩn, loại bỏ dầu, mỡ, hydroxit kim loại, chất keo, nhũ tương, chất rắn lơ lửng, v&agrave; hầu hết c&aacute;c ph&acirc;n tử lớn từ trước v&agrave; c&aacute;c dung dịch kh&aacute;c nhau như(phấn hoa, tảo, k&iacute; sinh tr&ugrave;ng, virut v&agrave; vi tr&ugrave;ng g&acirc;y bệnh&hellip;) v&agrave; đặc biệt l&agrave; c&oacute; thể triệt ti&ecirc;u được vi khuẩn tới 99.9% dường như kh&ocirc;ng c&ograve;n vi khuẩn. C&aacute;c ph&acirc;n tử c&oacute; k&iacute;ch thước lớn hơn như c&aacute;c loại tạp chất, virus, vi khuẩn sẽ bị giữ lại, qua tất cả c&aacute;c bước lọc khắt khe nhất từ c&aacute;c l&otilde;i lọc, cấp lọc v&agrave; m&agrave;ng lọc UF đ&atilde; cho ra một nguồn nước gi&agrave;u kho&aacute;ng chất đảm bảo sức khỏe tối ưu cho người sử dụng.</p>\r\n\r\n			<p><strong>Th&ocirc;ng số vận h&agrave;nh:</strong><br />\r\n			&ndash; Nhiệt độ hoạt động: 4-38 độ C<br />\r\n			&ndash; &Aacute;p lực c&ocirc;ng việc: 10-125 Psi<br />\r\n			&ndash; Lưu lượng d&ograve;ng chảy: 2 l&iacute;t/ ph&uacute;t</p>\r\n\r\n			<p>Tuổi thọ l&otilde;i lọc: 18-24 th&aacute;ng/ 7.200 l&iacute;t</p>\r\n			</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-2.jpg\" style=\"height:338px; width:81px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>4</strong></td>\r\n			<td><strong>L&otilde;i lọc Post Carbon Filter</strong>\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&ndash; L&otilde;i Post &ndash; carbon filter l&agrave; carbon c&oacute; hoạt t&iacute;nh cao dạng khối gi&uacute;p cải thiện m&ugrave;i vị, tạo độ ngon cho nước uống, độ trong của nước loại bỏ kim loại tồn dư gi&uacute;p nước đầu&nbsp;ra ngon ngọt v&agrave; tinh khiết hơn. Loại bỏ vị h&ocirc;i, m&ugrave;i lạ, sắc tố trong nước.</p>\r\n\r\n			<p>Tuổi thọ 15-18 th&aacute;ng ( Hoặc 5,400 l&iacute;t)</p>\r\n			</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-3.jpg\" style=\"height:296px; width:71px\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kết quả phân tích nước đầu ra, đảm bảo tiêu chuẩn nước uống trực tiếp\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/quatest-picogram-pnp.jpg\" style=\"height:576px; width:800px\" /></p>\r\n\r\n<p><em>Kết quả ph&acirc;n t&iacute;ch nước đầu ra, đảm bảo ti&ecirc;u chuẩn nước uống trực tiếp</em></p>\r\n\r\n<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i m&aacute;y lọc nước nguy&ecirc;n kho&aacute;ng Picogram UF:</strong></p>\r\n\r\n<p><img alt=\"\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2022/02/may-loc-nuoc-gia-dinh-han-quoc-picogram-pnp-hanquoc.jpg\" style=\"height:349px; width:500px\" /></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-1.png\" style=\"height:18px; width:18px\" />&nbsp; M&aacute;y Picogram nhập khẩu</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-1.png\" style=\"height:18px; width:18px\" />&nbsp;V&ograve;i cổ gỗng cao cấp</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-1.png\" style=\"height:18px; width:18px\" />&nbsp;Thiết bị chia t&aacute;ch nước</td>\r\n		</tr>\r\n		<tr>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-1.png\" style=\"height:18px; width:18px\" />&nbsp;Bộ d&acirc;y dẫn cấp nước</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/12/may-loc-nuoc-nguyen-khoang-picogram-uf-1.png\" style=\"height:18px; width:18px\" />&nbsp;S&aacute;ch hướng dẫn sử dụng</td>\r\n			<td><img alt=\"Máy lọc nước nguyên khoáng Picogram UF\" src=\"http://maylocnuocsmartviet.com/wp-content/uploads/2021/1